mapper.select:
select t.id, t1.name as systemName, t2.name as positionName, t.`code` as alarmCode, t.`name` as alarmName, t.reason, t3.name as alarmLevelName, t.handling_opinions from alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.position_id join alarm_level t3 on t3.id = t.level_id where t.is_deleted = 0
and t.system_id = #{systemId}
and t.code = #{alarmCode}
and t.name like concat('%',#{alarmName},'%')
and t.level_id = #{alarmLevelId}
limit #{page},#{size}

select count(1) from alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.position_id join alarm_level t3 on t3.id = t.level_id where t.is_deleted = 0
and t.system_id = #{systemId}
and t.code = #{alarmCode}
and t.name like concat('%',#{alarmName},'%')
and t.level_id = #{alarmLevelId}
