mapper.select:
SELECT ah.id,ah.subsystem_id,s.name as subsystem_name,ah.subsystem_code,ah.line_id,p1.name as line_name, ah.line_code,ah.site_id,p2.name as site_name,ah.frequency,ah.is_mute,ah.is_upgrade,ah.alarm_volume, ah.device_id,d.name as device_name,ah.alarm_level,ah.slot_id,ds.name as slot_position,ac.code as alarm_code, ah.alarm_name,ah.alarm_reason,ah.first_time,ah.final_time,ah.alarm_state,ah.recovery_time,ah.alarm_remark FROM alarm_history as ah left join subsystem as s on s.id=ah.subsystem_id and s.is_deleted=0 left join position as p1 on p1.id=ah.site_id and p1.is_deleted=0 left join position as p2 on p2.id=ah.site_id and p2.is_deleted=0 left join device as d on d.id=ah.device_id and d.is_deleted=0 left join device_slot as ds on ds.id=ah.slot_id and ds.is_deleted=0 left join alarm_code as ac on ac.id=ah.alarm_code and ac.is_deleted=0 where ah.is_deleted=0 and ah.alarm_state in (4,5,6,7)
and ah.subsystem_id=#{subsystemId}
and ah.site_id=#{siteId}
and ah.alarm_level=#{alarmLevel}
and ac.code=#{alarmCode}
and ah.final_time >= #{startTime}
and #{endTime} >= ah.final_time
ORDER BY ah.updated_at DESC

SELECT ah.id,ah.subsystem_id,s.name as subsystem_name,ah.subsystem_code,ah.line_id,p1.name as line_name, ah.line_code,ah.site_id,p2.name as site_name,ah.frequency, ah.device_id,d.name as device_name,ah.alarm_level,ah.slot_id,ds.name as slot_position,ac.code as alarm_code, ah.alarm_name,ah.alarm_reason,ah.first_time,ah.final_time,ah.alarm_state,ah.recovery_time,ah.alarm_remark FROM alarm_history as ah left join subsystem as s on s.id=ah.subsystem_id and s.is_deleted=0 left join position as p1 on p1.id=ah.site_id and p1.is_deleted=0 left join position as p2 on p2.id=ah.site_id and p2.is_deleted=0 left join device as d on d.id=ah.device_id and d.is_deleted=0 left join device_slot as ds on ds.id=ah.slot_id and ds.is_deleted=0 left join alarm_code as ac on ac.id=ah.alarm_code and ac.is_deleted=0 where ah.is_deleted=0 and ah.alarm_state in (4,5,6,7)
and ah.subsystem_id=#{subsystemId}
and ah.site_id=#{siteId}
and ah.alarm_level=#{alarmLevel}
and ah.alarm_code_id=#{alarmCode}
and ah.final_time >= #{startTime}
and #{endTime} >= ah.final_time
ORDER BY ah.updated_at DESC

mapper.insert:
delete from alarm_message
WHERE alarm_id=(select max(id) as id from alarm_history where subsystem_id=#{subsystemId} and line_id=#{lineId} and site_id=#{siteId}
and device_id=#{deviceId}

    and slot_id=#{slotId}

and alarm_code=#{alarmCode} and alarm_state=#{alarmState} and is_deleted=0) and is_deleted=0;
INSERT INTO alarm_message (alarm_id, title, content)
values

    ((select max(id) as id from alarm_history where subsystem_id=#{subsystemId} and line_id=#{lineId} and site_id=#{siteId}
    and device_id=#{deviceId}

        and slot_id=#{slotId}

    and alarm_code=#{alarmCode} and alarm_state=#{alarmState} and is_deleted=0),
    #{alarmMessage.title}, #{alarmMessage.content})

mapper.update:
UPDATE alarm_history SET alarm_level=alarm_level-1,is_upgrade=0 where is_deleted=0 and experience_time is not null
and alarm_level!=1 and now()>=date_add(first_time,interval experience_time minute_second) and is_upgrade=1 and alarm_state in (0,1,2,3);
UPDATE alarm_history SET alarm_level=alarm_level-1,is_upgrade=0 where is_deleted=0 and experience_time is not null
and alarm_level!=1 and now()>=date_add(date_add(first_time,interval experience_time minute_second),interval experience_time minute_second) and is_upgrade=0
and alarm_state in (0,1,2,3);
