mapper.update:
update alarm_rule
set is_deleted = 1
where id = #{id}

mapper.select:
select count(1) from alarm_rule where is_deleted = 0
and is_enable = #{isEnable}
and `type` = #{type}
and `name` like concat('%',#{name},'%')

select id, `type`, `name`, is_enable, DATE_FORMAT(created_at, "%Y-%m-%d %T") as createdAt, DATE_FORMAT(updated_at, "%Y-%m-%d %T") as updatedAt, created_by, updated_by from alarm_rule where is_deleted = 0
and is_enable = #{isEnable}
and `type` = #{type}
and `name` like concat('%',#{name},'%')
limit #{page},#{size}

select id, name from device


        system_id in (

            #{systemId}

        ) or


        station_id in (

            #{stationId}

        ) or

SELECT id,code as alarm_code,name as alarm_name FROM alarm_code

    where system_id in (

        #{systemId}

