mapper.select:
select cs.id as subsystem_id,cs.name as subsystem_name,p.id as site_id,p.name as site_name,
ah.alarm_name,ah.alarm_reason,count(ah.id) as alarm_num from alarm_history as ah
left join subsystem as cs on ah.subsystem_id=cs.id left join position as p on p.id=ah.site_id
where ah.is_deleted=0

    and cs.id=#{systemId}


    and p.id=#{siteId}


    and ah.alarm_reason=#{alarmReason}


    and ah.final_time>=#{startTime}


    and #{endTime}>=ah.final_time

group by cs.id,p.id

select p.id as site_id,p.name as site_name,
ah.alarm_level,count(ah.id) as alarm_num from alarm_history as ah
left join position as p on p.id=ah.site_id
where ah.is_deleted=0

    and p.id in(

        #{siteId}

    )


    and ah.alarm_level in(

        #{level}

    )


    and ah.final_time>=#{startTime}


    and #{endTime}>=ah.final_time

group by p.id,ah.alarm_level

select cs.id as subsystem_id,cs.name as subsystem_name,
ah.alarm_level,count(ah.id) as alarm_num from alarm_history as ah
left join subsystem as cs on ah.subsystem_id=cs.id
where ah.is_deleted=0

    and cs.id in(

        #{systemId}

    )


    and ah.alarm_level in(

        #{level}

    )


    and ah.final_time>=#{startTime}


    and #{endTime}>=ah.final_time

group by cs.id,ah.alarm_level

select ah.alarm_level,count(ah.id) as alarm_num from alarm_history as ah
where ah.is_deleted=0

    and ah.alarm_level in(

        #{level}

    )


    and ah.final_time>=#{startTime}


    and #{endTime}>=ah.final_time

group by ah.alarm_level

select ah.alarm_level,count(ah.id) as alarm_num,DATE_FORMAT(ah.final_time,

    '%Y年%m月%d日'


    '%Y年%u周'


    '%Y年%m月'

) as statistics_date from alarm_history as ah
left join position as p on p.id=ah.site_id
where ah.is_deleted=0

    and p.id=#{siteId}


    and ah.alarm_level in(

        #{level}

    )


    and ah.final_time>=#{startTime}


    and #{endTime}>=ah.final_time

group by p.id,ah.alarm_level,statistics_date

