select ah.alarm_level,TIMESTAMPDIFF(SECOND,ah.first_time,ah.recovery_time) as disposal_time,DATE_FORMAT(ah.final_time,

    '%Y年%m月%d日'


    '%Y年%u周'


    '%Y年%m月'

) as statistics_date from alarm_history as ah
where ah.is_deleted=0

    and ah.alarm_level in(

        #{level}

    )


    and ah.final_time>=#{startTime}


    and #{endTime}>=ah.final_time

group by ah.alarm_level,statistics_date
