mapper.update:
update device set `name` = #{reqModifyDTO.name}, system_id = #{reqModifyDTO.systemId}, position_id = #{reqModifyDTO.positionId}, device_code = #{reqModifyDTO.deviceCode}, brand = #{reqModifyDTO.brand}, serial_num = #{reqModifyDTO.serialNum}, description = #{reqModifyDTO.description}, updated_at = NOW()
,updated_by = #{userId}
where id = #{id}

mapper.select:
select t.id, t.name, t.system_id, t1.sid as system_code, t1.name as systemName, t.position_id, t2.position_code as position_code, t2.name as positionName, t.device_code, t.brand, t.serial_num, t.description from device t join subsystem t1 on t1.id = t.system_id join position t2 on t2.id = t.position_id where t.is_deleted = 0
and t.`name` like concat('%',#{name},'%')
and t.device_code = #{deviceCode}
and t.system_id = #{systemId}
and t.position_id = #{positionId}
limit ${page},${size}

select
    count(1)
from device t
join subsystem t1 on t1.id = t.system_id
join position t2 on t2.id = t.position_id
where t.is_deleted = 0

    and t.`name` like concat('%',#{name},'%')


    and t.device_code = #{deviceCode}


    and t.system_id = #{systemId}


    and t.position_id = #{positionId}
