mapper.update:
updata device_slot
set is_deleted = 1
where id = #{id}

mapper.select:
select
    t.id,
    t.slot_code as slotCode,
    t.`name` as slotName,
    t1.device_code,
    t1.`name` as deviceName,
    t2.`name` as positionName,
    t3.`name` as systemName
from device_slot t
left join device t1 on t.device_id = t1.id
left join `position` t2 on t1.position_id = t2.id
left join subsystem t3 on t1.system_id = t3.id
where t.is_deleted = 0

    and t.`name` = #{slotName}


    and t1.`name` = #{deviceName}


    and t1.device_code = #{deviceCode}


    and t3.id = #{systemId}


    and t2.id = #{positionId}


    limit #{pageReq.page},#{pageReq.size}

select
    count(1)
from device_slot t
left join device t1 on t.device_id = t1.id
left join `position` t2 on t1.position_id = t2.id
left join subsystem t3 on t1.system_id = t3.id
where t.is_deleted = 0

    and t.`name` = #{slotName}


    and t1.`name` = #{deviceName}


    and t1.device_code = #{deviceCode}


    and t3.id = #{systemId}


    and t2.id = #{positionId}
