mapper.update:
update device_slot
set is_deleted = 1
where id = #{id}

mapper.select:
select t.id, t.slot_code as slotCode, t.`name` as slotName, t.device_id, t.position_id, t.system_id, t1.device_code, t1.`name` as deviceName, t2.`name` as positionName, t3.`name` as systemName from device_slot t left join device t1 on t.device_id = t1.id and t1.is_deleted=0 left join `position` t2 on t1.position_id = t2.id and t2.is_deleted=0 left join subsystem t3 on t1.system_id = t3.id and t3.is_deleted=0 where t.is_deleted = 0
and t.`name` like concat('%',#{slotName},'%')
and t1.`name` like concat('%',#{deviceName},'%')
and t1.device_code = #{deviceCode}
and t3.id = #{systemId}
and t2.id = #{positionId}
limit #{page},#{size}

select count(1) from device_slot t left join device t1 on t.device_id = t1.id and t1.is_deleted=0 left join `position` t2 on t1.position_id = t2.id and t2.is_deleted=0 left join subsystem t3 on t1.system_id = t3.id and t3.is_deleted=0 where t.is_deleted = 0
and t.`name` like concat('%',#{slotName},'%')
and t1.`name` like concat('%',#{deviceName},'%')
and t1.device_code = #{deviceCode}
and t3.id = #{systemId}
and t2.id = #{positionId}
