mapper.select:
select t.id, t.`name` as snmpSlotName, t1.sid as systemId, t1.name as systemName, t5.position_code as positionId, t5.name as positionName, t2.position_code as siteCode, t2.name as siteName, t4.device_code as deviceCode, t3.slot_code as slotCode, t3.`name` as slotName from snmp_slot t join subsystem t1 on t.system_id = t1.id and t1.is_deleted=0 join `position` t2 on t.position_id = t2.id and t2.is_deleted=0 join `position` t5 on t2.p_id=t5.id and t5.is_deleted=0 join device_slot t3 on t.slot_id = t3.id and t3.is_deleted=0 join device t4 on t3.device_id = t4.id and t4.is_deleted=0 where t.is_deleted = 0
and t.`name` like concat('%',#{snmpSlotName},'%')
and t.system_id = #{systemId}
and t.position_id = #{siteId}
limit #{pageReq.page},#{pageReq.size}

select count(1) from snmp_slot t left join subsystem t1 on t.system_id = t1.id and t1.is_deleted=0 left join `position` t2 on t.position_id = t2.id and t2.is_deleted=0 left join device_slot t3 on t.slot_id = t3.id and t3.is_deleted=0 left join device t4 on t3.device_id = t4.id and t4.is_deleted=0 where t.is_deleted = 0
and t.`name` like concat('%',#{snmpSlotName},'%')
and t.system_id = #{systemId}
and t.position_id = #{siteId}

select t.id, t.system_id, t.`name` as snmpSlotName, t.position_id, t1.`name` as systemName, t.slot_id, t2.`name` as siteName, t4.`name` as deviceName, t3.slot_code as slotCode, t3.`name` as slotName from snmp_slot t left join subsystem t1 on t.system_id = t1.id and t1.is_deleted=0 left join `position` t2 on t.position_id = t2.id and t2.is_deleted=0 left join device_slot t3 on t.slot_id = t3.id and t3.is_deleted=0 left join device t4 on t3.device_id = t4.id and t4.is_deleted=0 where t.is_deleted = 0
and t.`name` like concat('%',#{snmpSlotName},'%')
and t.system_id = #{systemId}
and t.position_id = #{siteId}
limit #{page},#{size}

select t1.sid as system_code, t1.name as system_name, t2.position_code as position_code, t2.name as position_name, t.element_type, t.snmp_code, t.code, t.reason from snmp_alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.positionId where t.is_deleted = 0
and t.code = #{alarmCode}
and t.system_id = #{systemId}

select count(1) from snmp_alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.positionId where t.is_deleted = 0
and t.code = #{code}
and t.system_id = #{systemId}

select t.id, t.system_id, t.positionId, t1.sid as systemCode, t2.position_code as systemCode, t1.`name` as systemName, t2.`name` as positionName, t.element_type, t.snmp_code, t.code, t.reason from snmp_alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.positionId where t.is_deleted = 0
and t.code = #{code}
and t.system_id = #{systemId}
limit #{page},#{size}
