mapper.select:
select t.id, t.`name` as snmpSlotName, t1.id as systemId, t2.p_id as lineCode, t2.id as siteCode, t4.device_code as deviceCode, t3.id as slotCode, t3.`name` as slotName from snmp_slot t join subsystem t1 on t.system_id = t1.id join `position` t2 on t.position_id = t2.id join device_slot t3 on t.slot_id = t3.id join device t4 on t3.device_id = t4.id where t.is_deleted = 0
t.`name` like concat('%',#{snmpSlotName},'%')
t.system_id = #{systemId}
t.position_id = #{siteId}
limit #{pageReq.page},#{pageReq.size}

select count(1) from snmp_slot t join subsystem t1 on t.system_id = t1.id join `position` t2 on t.position_id = t2.id join device_slot t3 on t.slot_id = t3.id join device t4 on t3.device_id = t4.id where t.is_deleted = 0
t.`name` like concat('%',#{snmpSlotName},'%')
t.system_id = #{systemId}
t.position_id = #{siteId}

select t.id, t.`name` as snmpSlotName, t1.`name` as systemName, t2.`name` as siteName, t4.`name` as deviceName, t3.id as slotCode, t3.`name` as slotName from snmp_slot t join subsystem t1 on t.system_id = t1.id join `position` t2 on t.position_id = t2.id join device_slot t3 on t.slot_id = t3.id join device t4 on t3.device_id = t4.id where t.is_deleted = 0
t.`name` like concat('%',#{snmpSlotName},'%')
t.system_id = #{systemId}
t.position_id = #{siteId}
limit #{page},#{size}

select t1.sid, t2.position_code, t.element_type, t.snmp_code, t.code, t.reason from snmp_alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.positionId where t.is_deleted = 0
and t.code = #{alarmCode}
and t.system_id = #{systemId}

select count(1) from snmp_alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.positionId where t.is_deleted = 0
and t.code = #{code}
and t.system_id = #{systemId}

select t1.`name` as systemName, t2.`name` as positionName, t.element_type, t.snmp_code, t.code, t.reason from snmp_alarm_code t join subsystem t1 on t1.id = t.system_id join `position` t2 on t2.id = t.positionId where t.is_deleted = 0
and t.code = #{code}
and t.system_id = #{systemId}
limit #{page},#{size}
