mapper.insert:
insert into user(user_name, password,created_by,updated_by

    , user_real_name


    , phone


    , mail


    , status


    , remark

) value(#{user.userName},#{user.password},#{doName},#{doName}

    ,#{user.userRealName}


    , #{user.phone}


    , #{user.mail}


    , #{user.status}


    , #{user.remark}

);

mapper.select:
select * from user where is_deleted=0

    and status=#{status}


    and user_real_name like '%${userRealName}%'
